import React from 'react'
import { useCategories, useProjects } from '../requests'
import { Heading, Button, Grid, GridItem, Flex, Text } from '@chakra-ui/react'

const Dashboard = () => {
  const fetcher = (...args) => fetch(...args).then(res => res.json())
  const {data : categories, error : categoryError, isLoading : categoryLoading} = use
  const { data : projects, error : projectError, isLoading : projectLoading } = useSWR('http://localhost:8000/api/projects/', fetcher)

  //transfer to utils
  const categoryList = categories.map(category => {
    const projectsList = projects.map(project => {
      if(project.categoryID === category.categoryID)
      {
        return (
          <Text>
            {project.title}
          </Text>
        )
      }
    })
    return (
      <GridItem key={category.id}>
        <Heading>{category.title}</Heading>
        <Flex>
          {projectsList}
        </Flex>
      </GridItem>
    )
  })

  return (
    <div className='page' style={{ paddingTop: '8rem' }}>
      <Button colorScheme="primary">Start a new category</Button>
      <Heading marginTop='1rem'>Your projects</Heading>
      <Grid
        marginTop='2rem'
        width='100%'
        flexGrow={1}
        templateColumns='repeat(4, 1fr)'
        gap={4}
      >
        {categoryList}
      </Grid>
    </div>
  )
}

export default Dashboard
